import React from "react";

class App extends React.Component {
  constructor() {
    super()
    this.leftClick = this.leftClick.bind(this);
    this.centerClick = this.centerClick.bind(this);
    this.rightClick = this.rightClick.bind(this);

    this.state = {
      numeroDeCliques01: 0,
      numeroDeCliques02: 0,
      numeroDeCliques03: 0
    }
  }

  leftClick() {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques01: estadoAnterior.numeroDeCliques01 + 1
    }))
    console.log(`Clicou no botão esquerdo! ${this.getButtonColor(this.state.numeroDeCliques01)}`)
  }

  centerClick() {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques02: estadoAnterior.numeroDeCliques02 + 1
    }))
    console.log(`Clicou no botão central! ${this.getButtonColor(this.state.numeroDeCliques02)}`)
  }

  rightClick() {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques03: estadoAnterior.numeroDeCliques03 + 1
    }))
    console.log(`Clicou no botão direito! ${this.getButtonColor(this.state.numeroDeCliques03)}`)
  }

  getButtonColor(num) {
    return num % 2 === 0 ? 'green' : 'white';
  }


  render() {
    return (
      <>
        <button onClick={this.leftClick} style={{ backgroundColor: this.getButtonColor(this.state.numeroDeCliques01) }}>
          Meu botão Esquerdo {this.state.numeroDeCliques01}
        </button>
        <button onClick={this.centerClick} style={{ backgroundColor: this.getButtonColor(this.state.numeroDeCliques02) }}>
          Meu botão Central {this.state.numeroDeCliques02}
        </button>
        <button onClick={this.rightClick} style={{ backgroundColor: this.getButtonColor(this.state.numeroDeCliques03) }}>
          Meu botão Direito {this.state.numeroDeCliques03}
        </button>
      </>
    );
  }

}

export default App;
